mapper.select:
select o.id, o.number, o.status, o.user_id, o.address_book_id, o.order_time, o.checkout_time, o.pay_method, o.pay_status, o.amount, o.remark, o.phone, o.address, o.user_name, o.consignee, o.cancel_reason, o.rejection_reason, o.cancel_time, o.estimated_delivery_time, o.delivery_status, o.delivery_time, o.pack_amount, o.tableware_number, o.tableware_status from orders o
o.user_id = #{currentId}
and o.status = #{status}
order by o.order_time desc

SELECT DATE(order_time) AS date, SUM(CASE WHEN status = 5 THEN 1 ELSE 0 END) AS validOrderCount, COUNT(id) AS totalOrderCount FROM orders WHERE DATE(order_time) IN
#{date}
GROUP BY DATE(order_time)
